function Calculate(t){this.container=document.getElementById(t),this.btn=this.container.getElementsByTagName("input")[2],this.a=this.container.getElementsByTagName("input")[0],this.b=this.container.getElementsByTagName("input")[1],this.flag=this.container.getElementsByTagName("select")[0],this.result=this.container.getElementsByTagName("div")[0]}Calculate.prototype.sum=function(t,e){return parseInt(t)+parseInt(e)},Calculate.prototype.abstract=function(t,e){return parseInt(t)-parseInt(e)},Calculate.prototype.multiply=function(t,e){return parseInt(t)*parseInt(e)},Calculate.prototype.divide=function(t,e){return parseInt(t)/parseInt(e)},Calculate.prototype.mod=function(t,e){return parseInt(t)%parseInt(e)},Calculate.prototype.init=function(){var t=this;this.btn.onclick=function(){var e=t.a.value,a=t.b.value,n=t.flag.value,r=0;switch(parseInt(n)){case 1:r=t.sum(e,a);break;case 2:r=t.abstract(e,a);break;case 3:r=t.multiply(e,a);break;case 4:r=t.divide(e,a);break;case 5:r=t.mod(e,a)}console.log(t.result),t.result.innerHTML=r}};var Calculateobj=new Calculate("calculate");Calculateobj.init();